procedures.turn:
{ SET $turn = ($turn + 1);
SET $turns_count = ($turns_count - 1);
SET $minutes = (($turn %% $turns_per_hour) * (60 / $turns_per_hour));
SET $hour = (($turn / $turns_per_hour) %% 24);
SET $day = ($turn / ($turns_per_hour * 24));
IF ($turns_count > 0) THEN RUN turn ELSE { SET $turns_count = 1 } }
